mapper.update:
update task_child
set assignment_stage=#{assignmentStage}
where child_id=#{childId} and task_id=#{taskId};

update task_child
set volunteer_id=#{volunteerId}
where child_id=#{childId} and task_id=#{taskId};

mapper.select:
select *
from task_child
where child_id=#{childId} and task_id=#{taskId};

select *
from task_child
where task_id=#{taskId} and is_completed=#{isCompleted} and is_corrected=#{isCorrected}
limit #{pageNo},#{pageSize};

select count(*)
from task_child
where task_id=#{taskId} and is_completed=#{isCompleted} and is_corrected=#{isCorrected}
